import { useState } from "react";
import "./index.css"


const App = () => {
  const [clicks, setClicks] = useState({
    left: 0,
    right: 0
  })

  const handleLeftClick = () => {
    const newClicks = {
      left: clicks.left +1,
      right: clicks.right,
    }
    setClicks(newClicks)
  }


  const handleRightClick = () => {
    const newClicks = {
      left: clicks.left,
      right: clicks.right +1
    }
    setClicks(newClicks)
  }



  return (
    <div className="hovers">
      {clicks.left}
      <button class="hoverTest" style={{padding:"5px", width: "50px", border:"none", borderRadius: "2px", margin: "2px", background:"black", color:"#fff"}} onClick={handleLeftClick}>left</button>
      <button class="hoverTest" style={{padding:"5px", width: "50px", border:"none", borderRadius: "2px", margin: "2px", background:"black", color:"#fff"}} onClick={handleRightClick}>right</button>
      {clicks.right}
    </div>
  )
}
export default App;
