const Hello = (props) => {
  const bornYear = () => {
    const yearNow = new Date().getFullYear()
    return yearNow - props.age
  }

  return (
    <div>
      <p>Hello <code style={{fontWeight:"bolder"}}>{props.name}</code>, you are <code style={{fontWeight:"bolder"}}>{props.age}</code> years old</p>
      <p>So you were probably born in {bornYear()}</p>
    </div>
  )
}



const Footer = () => {
  return (
    <div style={{background:"#333", color:"#fff", padding:"15px", borderRadius: "5px"}}>
      greeting app created by <a style={{color:"red"}} href="https://github.com/mluukkai">mluukkai</a>
    </div>
  )
}

function App() {
  const name = "Peter"
  const age  = 10

  return (
    <div style={{textAlign:"center"}}>
      <h1>Greetings</h1>
      <hr style={{border: "2px solid black"}}/>
      <Hello name='Maya' age={26 + 10} />
      <hr style={{border: "2px solid black"}}/>
      <Hello name={name} age={age} />
      <br />
      <Footer />
    </div>
  );
}

export default App;
